import React, { useEffect, useState } from 'react'

function ContactPage() {
    var od = [
        
    ];
    useEffect(()=>{
        od = [{
            name:"Mert",
            surname:"Berber",
            email:"mertberber0142@gmail.com",
            message: "Deneme İçeriği"
        },
       ];
        setDatalar(od);
    },[]);
    const [datalar, setDatalar] = useState(od);
    useEffect(()=>{
        od = datalar;
    },[datalar]);


    function FormuGonder(){
        console.log(datalar);
        var name = document.getElementById("name").value;
        document.getElementById("name").value = "";
        var surname = document.getElementById("surname").value;
        document.getElementById("surname").value = "";
        var email = document.getElementById("email").value;
        document.getElementById("email").value = "";
        var message = document.getElementById("message").value;
        document.getElementById("message").value = "";
        datalar.push({name:name,surname:surname,email:email,message:message});
        console.log(datalar);
        setDatalar(datalar);
    }
  return (
    <div id="contactPage" className="pages">
        <div className="container">
            <div className="row">
                <div className='col-12 col-md-6 offset-md-3'>
                    <form id="contactForm">
                        <div className="form-group">
                            <label className="form-label">Adınız</label>
                            <input id="name" className="form-control" name="name" type="text"></input>
                        </div>
                        <div className="form-group">
                            <label className="form-label">Soyadınız</label>
                            <input id="surname" className="form-control" name="surname" type="text"></input>
                        </div>
                        <div className="form-group">
                            <label className="form-label">Email Adresiniz</label>
                            <input id="email" className="form-control" name="email" type="email"></input>
                        </div>
                        <div className="form-group">
                            <label className="form-label">Mesajınız</label>
                            <textarea id="message" className="form-control" name="message" rows="4"></textarea>
                        </div>
                        <div className="form-group">
                            <button onClick={FormuGonder} type="button" className="btn btn-outline-light">Formu Gönder</button>
                        </div>
                    </form>
                </div>
                {/* map */}
                <div className='col-12 col-md-6 offset-md-3'>
                    {

                        datalar.map((veri,i)=>{
                            return(
                                <div key={i} className="contactList" style={benimSitilim}>
                                    <div className="row">
                                        <div id="titleArea" className="col-12">
                                            {veri.name} {veri.surname} - {veri.email}
                                        </div>
                                        <div className="col-12">
                                            {veri.message}
                                        </div>
                                    </div>
                                </div>
                            )
                        })

                    }
                    
                </div>
            </div>
        </div>
    </div>
  )
}

var benimSitilim={
    backgroundColor:'#ffffff',
    borderRadius:'10px',
    marginBottom:'50px',
    color:'#000',
    padding:"10px"
};

export default ContactPage
