import React,{useState,useContext,useEffect} from "react";
import FollowMe from "./components/followme/FollowMe";
import Footer from "./globals/footer/Footer";
import Header from "./globals/header/Header";
import AboutPage from "./pages/aboutpage/AboutPage";
import HomePage from "./pages/homepage/HomePage";
import AppContext from "./AppContext";



//
function App() {

    const [birinciButonDegeri, setBirinciButonDegeri] = useState(0);
    const [ikinciButonDegeri, setİkinciButonDegeri] = useState(0);
    function effectFunc(text){
        alert(text);
    }
    //useEffect(()=>effectFunc("Her Yüklemede Render Edildi"));
    useEffect(()=>effectFunc("Birinci Butonun Değeri Değişti"),[birinciButonDegeri,ikinciButonDegeri]);



    function SayfaDegistirici(sayfa){
        setActivePage(sayfa);
    }
    function SayfayaTiklandi(tiklananSayfa){
        setClickedPageItem(tiklananSayfa);
    }
    const [activePage, setActivePage] = useState(<HomePage/>);
    const [clickedPageItem, setClickedPageItem] = useState(1);
    return(
        <>
            <AppContext.Provider value={{pageChanger:SayfaDegistirici,pageClick:SayfayaTiklandi,pageClicked:clickedPageItem}}>
                <button onClick={()=>{setBirinciButonDegeri(birinciButonDegeri+1);}}>Birinci Buton Tıklama Sayısı {birinciButonDegeri}</button>
                <button onClick={()=>{setİkinciButonDegeri(ikinciButonDegeri+1);}}>İkinci Buton Tıklama Sayısı {ikinciButonDegeri}</button>
                {/* <FollowMe />
                <Header />
                {activePage}
                <Footer /> */}
            </AppContext.Provider>
        </>
    );
}
export default App;
